mapper.select:

select 

,

from qrtz_job_details
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}

mapper.delete:

delete from qrtz_job_details
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}

mapper.update:

update qrtz_job_details
set DESCRIPTION = #{description,jdbcType=VARCHAR},
  JOB_CLASS_NAME = #{jobClassName,jdbcType=VARCHAR},
  IS_DURABLE = #{isDurable,jdbcType=VARCHAR},
  IS_NONCONCURRENT = #{isNonconcurrent,jdbcType=VARCHAR},
  IS_UPDATE_DATA = #{isUpdateData,jdbcType=VARCHAR},
  REQUESTS_RECOVERY = #{requestsRecovery,jdbcType=VARCHAR},
  JOB_DATA = #{jobData,jdbcType=LONGVARBINARY}
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}


update qrtz_job_details
set DESCRIPTION = #{description,jdbcType=VARCHAR},
  JOB_CLASS_NAME = #{jobClassName,jdbcType=VARCHAR},
  IS_DURABLE = #{isDurable,jdbcType=VARCHAR},
  IS_NONCONCURRENT = #{isNonconcurrent,jdbcType=VARCHAR},
  IS_UPDATE_DATA = #{isUpdateData,jdbcType=VARCHAR},
  REQUESTS_RECOVERY = #{requestsRecovery,jdbcType=VARCHAR}
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and JOB_NAME = #{jobName,jdbcType=VARCHAR}
  and JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}

mapper.select:
  	select 
	b.SCHED_NAME as schedName , b.TRIGGER_NAME as triggerName, b.TRIGGER_GROUP as triggerGroup, b.NEXT_FIRE_TIME as nextFireTime, b.PREV_FIRE_TIME as prevFireTime, b.PRIORITY as priority,
	b.TRIGGER_STATE as triggerState, b.TRIGGER_TYPE as triggerType, b.START_TIME as startTime, b.END_TIME as endTime,
	a.JOB_NAME as jobName, a.JOB_GROUP as jobGroup, a.DESCRIPTION as description, a.JOB_CLASS_NAME as jobClassName, a.IS_DURABLE as isDurable, a.REQUESTS_RECOVERY as requestsRecovery
	 from qrtz_job_details a , qrtz_triggers b
	where a.JOB_NAME = b.JOB_NAME and a.JOB_GROUP = b.JOB_GROUP
  	and a.JOB_NAME = #{jobName,jdbcType=VARCHAR}
  	and a.JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}
  	 limit #{start,jdbcType=INTEGER},  #{end,jdbcType=INTEGER}

  select count(*)
   from qrtz_job_details a , qrtz_triggers b
	where a.JOB_NAME = b.JOB_NAME and a.JOB_GROUP = b.JOB_GROUP
  	and a.JOB_NAME = #{jobName,jdbcType=VARCHAR}
  	and a.JOB_GROUP = #{jobGroup,jdbcType=VARCHAR}
